// Savolga eng mos javobni topish
// Math Solver Helper
function solveMath(expr: string): string | null {
  try {
    // Basic calculator parser
    // Replace "kopaygan", "zarb", "x", "*"
    const clean = expr.toLowerCase()
      .replace(/kopaygan/g, '*')
      .replace(/zarb/g, '*')
      .replace(/x/g, '*')
      .replace(/bo'lingan/g, '/')
      .replace(/bolingan/g, '/')
      .replace(/qoshilgan/g, '+')
      .replace(/ayrilgan/g, '-')
      .replace(/plus/g, '+')
      .replace(/minus/g, '-')
      // Remove non-math chars except numbers and operators
      .replace(/[^0-9\+\-\*\/\.\(\)%]/g, '');

    if (!clean || !/\d/.test(clean)) return null;
    
    // Safety check - simple eval replacement
    const result = new Function('return ' + clean)();
    
    if (isNaN(result) || !isFinite(result)) return null;
    return `Hisob-kitob natijasi: ${clean} = ${result}`;
  } catch {
    return null;
  }
}

export function findBestAnswer(question: string): string {
  const q = question.toLowerCase();

  // 1. Math Check (Before KB)
  if (/\d/.test(q) && (q.includes('+') || q.includes('-') || q.includes('*') || q.includes('/') || q.includes('x') || q.includes('kopaygan') || q.includes('bolingan'))) {
      const mathResult = solveMath(q);
      if (mathResult) return mathResult;
  }

  // 2. KB Search
  let bestMatch = { score: 0, answer: '' };
  
  for (const item of knowledgeBase) {
    let score = 0;
    for (const keyword of item.keywords) {
      if (q.includes(keyword.toLowerCase())) {
        score += keyword.length; 
      }
    }
    
    if (score > bestMatch.score) {
      bestMatch = { score, answer: item.answer };
    }
  }
  
  // 3. Fallback
  if (bestMatch.score === 0) {
    // Contextual Fallbacks for conversational fillers
    if (q.includes('yog\'e') || q.includes('yo\'g\'e') || q.includes('jiddiy') || q.includes('rost')) {
        return "Ha, albatta! Men har doim aniq ma'lumotlarga asoslanib javob beraman.";
    }
    if (q.includes('zerik') || q.includes('gaplash')) {
        return "Zerikdingizmi? Keling, moliyaviy o'yin o'ynaymiz! 'Agar menda 1 million dollar bo'lsa...' deb boshlang, men esa uni qanday ko'paytirishni aytaman!";
    }
    return knowledgeBase[knowledgeBase.length - 1].answer;
  }
  
  return bestMatch.answer;
}
